import React, { useState, useEffect } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import IconButton from '@material-ui/core/IconButton';
import AddIcon from '@material-ui/icons/Add';
import SendIcon from '@material-ui/icons/Send';
import ClearIcon from '@material-ui/icons/Clear';
import styled from 'styled-components';
import Picker from 'emoji-picker-react';
import bp from '../../Theme/breakpoints';
import theme from '../../Theme/theme';
import Footer from '../../Navigation/Footer/Footer';

const EmojiPicker = styled.div`
	z-index: 10;
	float: right;
`;

const QuizForm = styled.div`
	width: 90%;
	background-color: #5abe93;
	height: 600px;
	padding: 20px;
	border: solid #fff;
	border-width: 2px;
	border-radius: 15px;
	margin-bottom: 40px;
	margin-left: 20px;
	text-align: left;
	display: block;
	@media ${bp.sm} {
		width: 40%;
	}
`;

const MyProfile = styled.div`
	width: 90%;
	display: block;
	background-color: #b2d9b7;
	height: 600px;
	padding: 20px;
	text-align: left;
	border: solid #fff;
	border-width: 2px;
	border-radius: 15px;
	margin-bottom: 40px;
	margin-left: 20px;
	@media ${bp.sm} {
		width: 40%;
	}
`;

const FormLabel = styled.div`
	display: inline-block;
	width: 140px;
	text-align: left;
`;

const QuizItemHeader = styled.div`
	vertical-align: right;
	display: flex;
	width: 55%;
`;

const QuizEmojiHeader = styled.div`
	vertical-align: right;
	display: flex;
	width: 45%;
`;

const QuizPageHeading = styled.h1`
	padding-top: 50px;
	font-size: 72px;
	color: #fff;
`;

const UserDashboard = styled.div`
	text-align: center;
	background: ${theme.color.background.primary};
`;

const UserDashboardContent = styled.div`
	display: block;
	margin-bottom: 100px;
	margin-top: 100px;
	align-items: center;
	flex-direction: column;
	@media ${bp.sm} {
		display: flex;
		justify-content: center;
		flex-direction: row;
	}
`;

const Options = styled.div`
	display: flex;
`;

const ItemsContainer = styled.div`
	overflow-y: scroll;
	height: 50%;
`;

const QuizURLSection = styled.div`
	margin-top: 30px;
`;

const QuizTitleSection = styled.div`
	display: flex;
	align-items: flex-end;
	margin-bottom: 20px;
`;

const QuizPrivacySelector = styled.div`
	display: flex;
	align-items: flex-end;
	margin-bottom: 20px;
`;

const FormOptionsSection = styled.div`
	justify-content: flex-end;
	display: flex;
	text-align: left;
`;

const TextInput = styled.input`
	vertical-align: middle;
	border-radius: 20px;
	width: 60%;
	border: solid #fff;
	border-width: 0px;
	height: 30px;
	color: #000000;
	margin-top: 20px;
	margin-left: 20px;
	font-size: 18px;
`;

const SelectWrapper = styled.div`
	border-radius: 36px;
	width: 60%;
	display: inline-block;
	overflow: hidden;
	background: #ffffff;
	border: 0px solid #ffffff;
	margin-left: 20px;
